package ex_04_Increment_Decrement;

public class Lab026_ID_Advance_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		System.out.println(a++ + a);
		System.out.println(a);
		//A+B A->a++ Exp A -> 10 -> a=11
		//B->  a -> Exp B -> 11 , a=11
		
        // Line No. |  a |  Exp
        // 5 | 10 | NA |
        // 6 | 11 | 10+11 -> 21
	}

}
