package ex_04_Increment_Decrement;

public class Lab_027_ID_Advance_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		System.out.println(a++ + ++a);
		System.out.println(a);
		// A-> a++ -> exp A -> a=11
		// +
		// B-> ++a -> exp B -> a=12

	}

}
