package ex_04_Increment_Decrement;

public class Lab_028_ID_Advance_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		System.out.println(++a + ++a);
		System.out.println(a);
		// A-> ++a -> exp A = 11 -> a=11,
		// +
		// B-> ++a -> exp B = 12 -> a=12


	}

}
