package ex_07_For_Loop;

public class Lab_045_ForLoop001 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// for loop helps repeat a block of code multiple times
		// for(I;C;U) {} -> Intialization, condition, updation 
		for (int i=0 ;i<10 ;i++)
		{
			System.out.println(i);
		}
			
		

	}

}
