package ex_07_For_Loop;

public class Lab_048_For_If_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int shruti = 0; shruti<20; shruti++)
		{
			if(shruti>18)
			{
				System.out.println("Shruti is an adult");
				
			}
			else
			{
				System.out.println("Shruti is a teenager");
		}
	}

}
}