package ex_07_For_Loop;

public class Lab_050_For_Break {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1; i <= 50; i++)
		{
			if (i == 10)
				break; //break is used to come out of the loop
			
			 else { 
				 System.out.println(i); 
				 }
			 
		}
	}

}
