package ex_13_Arrays;

public class Lab_084_Array_Reverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int [] array1 = {11,12,15,19,17,20};
		
		for (int i = array1.length-1;i>=0;i--) //array1.length-1 as we are starting from the end
		{
			System.out.println(array1[i]);
		}

	}

}
