package javabasics;

public class Lab003_TableofNine_printf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		System.out.printf("%d x %d = %d", a,1,a*1);
		System.out.printf("\n%d x %d = %d", a,2,a*2);
		System.out.printf("\n%d x %d = %d", a,3,a*3);
		System.out.printf("\n%d x %d = %d", a,4,a*4);
		System.out.printf("\n%d x %d = %d", a,5,a*5);
		System.out.printf("\n%d x %d = %d", a,6,a*6);
		System.out.printf("\n%d x %d = %d", a,7,a*7);
		System.out.printf("\n%d x %d = %d", a,8,a*8);
		System.out.printf("\n%d x %d = %d", a,9,a*9);
		System.out.printf("\n%d x %d = %d", a,10,a*10);
		

	}

}
